var phantom = require("phantom");
var _ph, _page, _outObj;

phantom.create().then(ph => {
    _ph = ph;
    return _ph.createPage();
}).then(page => {
    _page = page;
    _page.property('paperSize',{ width: '595px', height: '842px', margin: '0px' })
    //_page.paperSize = { width: '595px', height: '842px', margin: '0px' };
    return _page.open('https://stackoverflow.com/');
}).then(status => {
    console.log(status);
     _page.render('stackoverflow.pdf');
    return _page.property('content')
}).then(content => {
    console.log(content);
    _page.close();
    _ph.exit();
}).catch(e => console.log(e));